    private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
if(txtcontact.getText().length()!=10)
{
    JOptionPane.showMessageDialog(rootPane, "Invalid contact no.");
    txtcontact.requestFocus();
}
else{
   try
    {
    int tcid;
    String tname,taddress,tcontact;
    tcid=Integer.parseInt(txtcid.getText());
    tname=txtcname.getText();
    taddress=txtaddress.getText();
    tcontact=txtcontact.getText();
    Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
    Connection con=DriverManager.getConnection("jdbc:odbc:stock");
    Statement stmt=con.createStatement();
    String query="insert into customertable values("+tcid+",'"+tname+"','"+tcontact+"','"+taddress+"')";
    stmt.executeUpdate(query);
    JOptionPane.showMessageDialog(null,"Data stored");
    txtcid.setText("");
    txtcname.setText("");
    txtaddress.setText("");
    txtcontact.setText("");
    txtcid.requestFocus();
}
catch(Exception ex)
{}
   
}
    }//GEN-LAST:event_addActionPerformed

