    private void formWindowActivated(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowActivated
 try
{
    int tcid=0;
    
    Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");
    Connection con=DriverManager.getConnection("jdbc:odbc:stock");
    Statement stmt=con.createStatement();
    String query="select * from customertable";
    ResultSet res=stmt.executeQuery(query);
    while(res.next())
    {
        tcid=Integer.parseInt(res.getString(1));
    }
    txtcid.setText(String.valueOf(tcid+1));
    txtcname.requestFocus();
}
catch(Exception ex)
{}
    }//GEN-LAST:event_formWindowActivated

    private void txtcnameKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtcnameKeyTyped
        char ch;
        ch=evt.getKeyChar();
        if(!(ch>='A'&&ch<='Z'||ch>='a'&&ch<='z'||ch==KeyEvent.VK_SPACE||ch==KeyEvent.VK_BACK_SPACE||ch==KeyEvent.VK_ENTER)) {
            JOptionPane.showMessageDialog(null, "Invalid Name.");
            evt.consume();
            txtcname.requestFocus();
        }
    }//GEN-LAST:event_txtcnameKeyTyped

    private void txtaddressKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtaddressKeyTyped
      
    }//GEN-LAST:event_txtaddressKeyTyped

    private void txtcontactKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtcontactKeyTyped
        char ch;
        ch=evt.getKeyChar();
        if(!(ch>='0'&&ch<='9'||ch==KeyEvent.VK_ENTER||ch==KeyEvent.VK_BACK_SPACE)) {
            JOptionPane.showMessageDialog(null, "Invalid No.");
            evt.consume();
            txtcontact.requestFocus();
        }
    }//GEN-LAST:event_txtcontactKeyTyped

